LocRegRadiusFeature::createFeature(Mat&amp; srcImg) {</code></p><p><code>//cvtColor(srcImg, srcImg, COLOR_RGB2GRAY);/* Get the single channel img */</code></p><p><code>srcImg.convertTo(srcImg, CV_64F);</code></p><p><code>vector&lt;Mat&gt; channels;</code></p><p><code>channels.reserve(srcImg.channels());</code><code>/* assign the memory space */</code></p><p><code>split(srcImg, channels);</code><code>/* save the each channel */</code></p><p><code>srcImg = channels[0];</code><code>/* only need the first channel */</code></p><p><code>int</code> <code>h = srcImg.rows, w = srcImg.cols;</code></p><p><code>if</code> <code>(h &gt;500 &amp;&amp; w &lt; 600)</code></p><p><code>h = 768, w = 516;</code></p><p><code>if</code> <code>(w &gt; 1000)</code></p><p><code>h = 684, w = 1024;</code></p><p><code>resize(srcImg, srcImg, Size(w, h), 0, 0, INTER_LINEAR);</code><code>/* Double LINEAR Way */</code></p><p>&nbsp;</p><p><code>Mat locRegRadiusMat = Mat(h, w, CV_64F, Scalar(0.0));</code></p><p><code>double</code> <code>*srcImgPtr = srcImg.ptr&lt;</code><code>double</code><code>&gt;(0);</code></p><p><code>double</code> <code>*l_R_R_M_Ptr = locRegRadiusMat.ptr&lt;</code><code>double</code><code>&gt;(0);</code></p><p><code>for</code> <code>(</code><code>auto</code> <code>x = 1; x &lt; (h - 1); ++x) {</code></p><p><code>srcImgPtr = srcImg.ptr&lt;</code><code>double</code><code>&gt;(x);</code></p><p><code>l_R_R_M_Ptr = locRegRadiusMat.ptr&lt;</code><code>double</code><code>&gt;(x);</code></p><p><code>for</code> <code>(</code><code>auto</code> <code>y = 1; y &lt; w - 1; ++y) {</code></p><p><code>if</code> <code>(1 == srcImgPtr[y]) {</code></p><p><code>continue</code><code>;</code></p><p><code>}</code></p><p><code>double</code> <code>sum_dist = 0;</code></p><p><code>vector&lt;</code><code>int</code><code>&gt; oriCloVec(8);</code><code>/* 8 directions, 1 means closed, 0 is opposite */</code></p><p>&nbsp;</p><p><code>int</code> <code>flagUP = 1, flagDOWN = 1, flagLEFT = 1, flagRIGHT = 1;</code></p><p><code>int</code> <code>flagRIGHT_UP = 1, flagLEFT_UP = 1, flagLEFT_DOWN = 1, flagRIGHT_DOWN = 1;</code></p><p><code>/* scan the 8 directions */</code></p><p><code>int</code> <code>i = x, j = y + 1;</code><code>/* anti-clockwise direction */</code></p><p><code>srcImgPtr = srcImg.ptr&lt;</code><code>double</code><code>&gt;(i);</code></p><p><code>while</code> <code>(flagRIGHT) {</code></p><p><code>if</code> <code>(0 != srcImgPtr[j]) {</code></p><p><code>flagRIGHT = 0;</code></p><p><code>//oriCloVec.insert(oriCloVec.begin(), 1);/* oriCloVec[0] = 1 */</code></p><p><code>oriCloVec[0] = 1;</code></p><p><code>}</code></p><p><code>if</code><code>(j &lt; w - 1) ++j;</code></p><p><code>else</code> <code>flagRIGHT = 0;</code></p><p><code>}</code></p><p><code>sum_dist = sum_dist + </code><code>sqrt</code><code>(</code><code>pow</code><code>(x - i, 2) + </code><code>pow</code><code>(y - j, 2));</code></p><p>&nbsp;</p><p><code>i = x - 1; j = y + 1;</code></p><p><code>srcImgPtr = srcImg.ptr&lt;</code><code>double</code><code>&gt;(i);</code></p><p><code>while</code> <code>(flagRIGHT_UP) {</code></p><p><code>if</code> <code>(0 != srcImgPtr[j]) {</code></p><p><code>flagRIGHT_UP = 0;</code></p><p><code>//oriCloVec.insert(oriCloVec.begin() + 1, 1);/* oriCloVec[1] = 1 */</code></p><p><code>oriCloVec[1] = 1;</code></p><p><code>}</code></p><p><code>if</code> <code>(i &gt; 0 &amp;&amp; j &lt; w - 1) { --i, ++j; }</code></p><p><code>else</code> <code>flagRIGHT_UP = 0;</code></p><p><code>}</code></p><p><code>sum_dist = sum_dist + </code><code>sqrt</code><code>(</code><code>pow</code><code>(x - i, 2) + </code><code>pow</code><code>(y - j, 2));</code></p><p>&nbsp;</p><p><code>i = x - 1; j = y;</code></p><p><code>srcImgPtr = srcImg.ptr&lt;</code><code>double</code><code>&gt;(i);</code></p><p><code>while</code> <code>(flagUP) {</code></p><p><code>if</code> <code>(0 != srcImgPtr[j]) {</code></p><p><code>flagUP = 0;</code></p><p><code>//oriCloVec.insert(oriCloVec.begin() + 2, 1);/* oriCloV