><p><code>if</code> <code>(0 != srcImgPtr[j]) {</code></p><p><code>flagLEFT_DOWN = 0;</code></p><p><code>//oriCloVec.insert(oriCloVec.begin() + 5, 1);/* oriCloVec[5] = 1 */</code></p><p><code>oriCloVec[5] = 1;</code></p><p><code>}</code></p><p><code>if</code> <code>(i &lt; (h - 1) &amp;&amp; j &gt; 0) ++i, --j;</code></p><p><code>else</code> <code>flagLEFT_DOWN = 0;</code></p><p><code>}</code></p><p><code>sum_dist = sum_dist + </code><code>sqrt</code><code>(</code><code>pow</code><code>(x - i, 2) + </code><code>pow</code><code>(y - j, 2));</code></p><p>&nbsp;</p><p><code>i = x + 1, j = y;</code></p><p><code>srcImgPtr = srcImg.ptr&lt;</code><code>double</code><code>&gt;(i);</code></p><p><code>while</code> <code>(flagDOWN) {</code></p><p><code>if</code> <code>(0 != srcImgPtr[j]) {</code></p><p><code>flagDOWN = 0;</code></p><p><code>//oriCloVec.insert(oriCloVec.begin() + 6, 1);/* oriCloVec[6] = 1 */</code></p><p><code>oriCloVec[6] = 1;</code></p><p><code>}</code></p><p><code>if</code> <code>(i &lt; (h - 1)) ++i;</code></p><p><code>else</code> <code>flagDOWN = 0;</code></p><p><code>}</code></p><p><code>sum_dist = sum_dist + </code><code>sqrt</code><code>(</code><code>pow</code><code>(x - i, 2) + </code><code>pow</code><code>(y - j, 2));</code></p><p>&nbsp;</p><p><code>i = x + 1, j = y + 1;</code></p><p><code>srcImgPtr = srcImg.ptr&lt;</code><code>double</code><code>&gt;(i);</code></p><p><code>while</code><code>(flagRIGHT_DOWN){</code></p><p><code>if</code><code>(0 != srcImgPtr[j]){</code></p><p><code>flagRIGHT_DOWN = 0;</code></p><p><code>//oriCloVec.insert(oriCloVec.begin() + 7, 1);/* oriCloVec[7] = 1 */</code></p><p><code>oriCloVec[7] = 1;</code></p><p><code>}</code></p><p><code>if</code> <code>(i &lt; (h - 1) &amp;&amp; j &lt; (w - 1)) ++i, ++j;</code></p><p><code>else</code> <code>flagRIGHT_DOWN = 0;</code></p><p><code>}</code></p><p><code>sum_dist = sum_dist + </code><code>sqrt</code><code>(</code><code>pow</code><code>(x - i, 2) + </code><code>pow</code><code>(y - j, 2));</code></p><p>&nbsp;</p><p><code>double</code> <code>radius_AVG = sum_dist / 8;</code></p><p>&nbsp;</p><p><code>/*---- calculate the closure weight ----*/</code></p><p><code>double</code> <code>closureW = </code><code>exp</code><code>(std::accumulate(oriCloVec.begin(), oriCloVec.end(), 0) - 8);</code></p><p><code>/*---- calculate the closure and the local region Radius ----*/</code></p><p><code>l_R_R_M_Ptr[y] = closureW / (radius_AVG + 150);</code></p><p>&nbsp;</p><p><code>}</code></p><p><code>}</code></p><p>&nbsp;</p><p><code>/*---- Blur ----*/</code></p><p><code>double</code> <code>sigma = 7; </code><code>int</code> <code>N = 30; </code><code>int</code> <code>N_row = 2 * N - 1;</code></p><p><code>cv::GaussianBlur(locRegRadiusMat, locRegRadiusMat, Size(N_row,N_row), sigma, sigma, 4);</code></p><p><code>/*---- Norm ----*/</code></p><p><code>double</code> <code>minValue = 0, maxValue = 0;</code></p><p><code>cv::minMaxLoc(locRegRadiusMat, &amp;minValue, &amp;maxValue);</code></p><p><code>cout &lt;&lt; minValue &lt;&lt; </code><code>&quot; &quot;</code> <code>&lt;&lt; maxValue &lt;&lt; endl;</code></p><p><code>locRegRadiusMat = (locRegRadiusMat - Mat(Size(w,h), CV_64F, Scalar(minValue))) / (maxValue - minValue);</code></p><p><code>return</code> <code>locRegRadiusMat;</code></p><p><code>}</code></p></td></tr></tbody></table><p>Main.cpp</p><table><tbody><tr><td><p>1</p><p>2</p><p>3</p><p>4</p><p>5</p><p>6</p><p>7</p><p>8</p><p>9</p><p>10</p><p>11</p><p>12</p><p>13</p><p>14</p><p>15</p><p>16</p><p>17</p><p>18</p><p>19</p><p>20</p><p>21</p><p>22</p><p>23</p><p>24</p><p>25</p><p>26</p></td><td><p><code>#paragma once</code></p><p><code>#include&quot;Feature.h&quot;</code></p><p><code>#include&quot;CenterFeature.h&quot;</code></p><p><code>#include&quot;ContDensityFeature.h&quot;</code></p><p><code>#include&quot;LocRegRadiusFeature.h&quot;</code></p><p><code>using</code> <code>namespace</code> <code>cv ;</code></p><p><code>using</code> <code>namespace</code> <code>std 